afterSetStayOnHover(value, oldValue) {
    if (value) {
        let me           = this,
            domListeners = me.domListeners || [];

        domListeners.push(
            {mouseenter: me.onMouseEnter, scope: me},
            {mouseleave: me.onMouseLeave, scope: me}
        );

       me.domListeners = domListeners;
   }
}

